  o = 0,
  c = 0;
export default class App extends Component {
  loadFunction = () => {
    $(".load-dyn")
      .html(s[t % s.length])
      .hide()
      .fadeIn();
    t++;
    i = setTimeout(o, 5500);
  };

  animateFunction = () => {};

  componentDidMount() {
    document.body.classList.add("index");
    this.loadFunction();
    this.animateFunction();
  }

  render() {
    return (
      <div>
        <MainComponent />
