import React, { Component } from "react";
import MainComponent from "./modules/Main/Desktop/MainComponent";
import MainMobileComponent from "./modules/Main/Mobile/MainComponent";
var $ = window.$;
var s = [
    "Loading...",
    "Still loading...",
    "Have patience, trying to load...",
    "Don't worry just a few seconds more...",
    "Connection seems to be slow..."
  ],
  t = 0,
  e = 0,
  i = 0,
  o = 0,
  c = 0;
export default class App extends Component {
  loadFunction = () => {
    $(".load-dyn")
      .html(s[t % s.length])
      .hide()
      .fadeIn();
    t++;
    i = setTimeout(o, 5500);
  };

  animateFunction = () => {};

  componentDidMount() {
    document.body.classList.add("index");
    this.loadFunction();
    this.animateFunction();
    if(window.screen.width <= 780){
    document.body.classList.add("mobile");
    }
  }

  render() {
    return (
      <div>
       {document.body.classList.indexOf("mobile") === -1 ? <MainComponent />: <MainMobileComponent />}
      </div>
    );
  }
}
